mapper.select:
SELECT DISTINCT RES.REV_ , HIP.BUSINESS_KEY_, DEF.NAME_ as procDefName, RES.ID_ , RES.NAME_, RES.PARENT_TASK_ID_, RES.DESCRIPTION_, RES.PRIORITY_, RES.CREATE_TIME_, RES.OWNER_, RES.ASSIGNEE_, RES.DELEGATION_, RES.EXECUTION_ID_, RES.PROC_INST_ID_, RES.PROC_DEF_ID_, RES.CASE_EXECUTION_ID_, RES.CASE_INST_ID_, RES.CASE_DEF_ID_, RES.TASK_DEF_KEY_, RES.DUE_DATE_, RES.FOLLOW_UP_DATE_, RES.SUSPENSION_STATE_, RES.TENANT_ID_ FROM ACT_RU_TASK RES LEFT JOIN act_hi_procinst HIP on RES.PROC_INST_ID_ = HIP.PROC_INST_ID_ LEFT JOIN act_re_procdef DEF on RES.PROC_DEF_ID_ = DEF.ID_ WHERE RES.ASSIGNEE_ = #{assignee,jdbcType=VARCHAR}
and HIP.BUSINESS_KEY_ like concat("%",#{name},"%")
ORDER BY RES.CREATE_TIME_ DESC LIMIT #{startIndex},#{pageSize}

SELECT DISTINCT RES.REV_ , HIP.BUSINESS_KEY_, DEF.NAME_ as procDefName, RES.ID_ , RES.NAME_, RES.PARENT_TASK_ID_, RES.DESCRIPTION_, RES.PRIORITY_, RES.CREATE_TIME_, RES.OWNER_, RES.ASSIGNEE_, RES.DELEGATION_, RES.EXECUTION_ID_, RES.PROC_INST_ID_, RES.PROC_DEF_ID_, RES.CASE_EXECUTION_ID_, RES.CASE_INST_ID_, RES.CASE_DEF_ID_, RES.TASK_DEF_KEY_, RES.DUE_DATE_, RES.FOLLOW_UP_DATE_, RES.SUSPENSION_STATE_, RES.TENANT_ID_ FROM ACT_RU_TASK RES LEFT JOIN act_hi_procinst HIP on RES.PROC_INST_ID_ = HIP.PROC_INST_ID_ LEFT JOIN act_re_procdef DEF on RES.PROC_DEF_ID_ = DEF.ID_ WHERE RES.ASSIGNEE_ = #{assignee,jdbcType=VARCHAR}
and HIP.BUSINESS_KEY_ =#{sampleInfo,jdbcType=VARCHAR}
and RES.PROC_DEF_ID_ =#{procDefId,jdbcType=VARCHAR}
and RES.NAME_ =#{nodeName,jdbcType=VARCHAR}
ORDER BY RES.CREATE_TIME_ DESC
